import React, { Component } from 'react';
import CardMenu from './CardMenu.js';

class MenuGallery extends Component {
  constructor(props){
    super(props);

    this.state = {
      cardMenu: false,
      menuIndex: 0
    }

    this.showMenu = this.showMenu.bind(this);
  }

  showMenu(index) {
    console.log(index);
    this.setState({
      cardMenu: true
    }) 
  }
  render() {
    let cardMenu = "";

    if(this.state.cardMenu){
      cardMenu = <CardMenu  menuIndex = {this.state.menuIndex}/>;
    }

    return (
      <div>
        <div className="row">
          <div className="col-6 gallery-image gallery-image-left" onClick={this.showMenu(0)}>
            <img src={require('../Assets/Images/omOss.jpg')} alt="bild pÃ¥ personal" />
            <p className="menu-gallery-title">Milkshakes</p>
          </div>
          <div className="col-6 gallery-image gallery-image-right" onClick={this.showMenu(1)}>
            <img src={require('../Assets/Images/omOss.jpg')} alt="bild pÃ¥ personal" />
            <p className="menu-gallery-title">Wraffles</p>
          </div>
        </div>

        <div className="row">
          <div className="col-12 gallery-image gallery-image-large" onClick={this.showMenu(2)}>
            <img src={require('../Assets/Images/omOss.jpg')} alt="bild pÃ¥ personal" />
            <p className="menu-gallery-title menu-gallery-title-large">VÃ¥fflor</p>
          </div>
        </div>

        <div className="row">
          <div className="col-6 gallery-image gallery-image-left" onClick={this.showMenu(3)}>
            <img src={require('../Assets/Images/omOss.jpg')} alt="bild pÃ¥ personal" />
            <p className="menu-gallery-title">Dryck</p>
          </div>
          <div className="col-6 gallery-image gallery-image-right" onClick={this.showMenu(4)}>
            <img src={require('../Assets/Images/omOss.jpg')} alt="bild pÃ¥ personal" />
            <p className="menu-gallery-title">Bakverk</p>
          </div>
        </div>

        {cardMenu}

      </div>

      )
  }
}

export default MenuGallery;
