import React, { Component } from 'react';

class ContactForm extends Component {
  constructor(props){
    super(props);


    this.handleFormInputs = this.handleFormInputs.bind(this);
    this.sendMessageToEmail = this.sendMessageToEmail.bind(this);
  }


handleFormInputs(e){
  e.preventDefault();
  console.log(e.target.value);

}

//Sends message to email adress through PHP 
  sendMessageToEmail(e) {
    e.preventDefault(); // Prevents page to reload when click on send button

    let contactFormInfo = {
        userEmail: "vanja.i@hotmail.com",
        userName: "Vanja",
        userMessage: "Hej, en fråga angående era öppettider"
    }


    console.log(contactFormInfo);

//Sending the contact information to php, that is stored in the object 'contactFormInfo', with a post method
  return fetch('http://localhost/Server/SendEmail.php', {
    method: "POST",
    mode: "no-cors",
    body: JSON.stringify(contactFormInfo)
    })
    .then((response) => {
      console.log(response);
    })
    .catch((error) => {
      console.error(error);
    })
}


  render() {
    return (

    	<div className="contact-form">
          <h2 className="second-section-title">Kontakta oss</h2>
	        <form>
              <div className="row">
                <div className="col-11 input-contact">
      	          <label>
                  <input type="text" name="name" placeholder="Namn" onChange={this.handleFormInputs} required />
                  </label>
                </div>
              </div>

              <div className="row"> 
              <div className="col-11 input-contact">
                <label>
                <input type="text" name="email" placeholder="Email" onChange={this.handleFormInputs} required />
                </label>
              </div>
              </div>

              <div className="row">
                <textarea className="col-11 input-contact" placeholder="Skriv ditt meddelande" onChange={this.handleFormInputs} required></textarea>
              </div>

              <div className="row">
                <div className="col-2 checkbox-gdpr">
                  <input className="checkbox" type="checkbox" required/>
                </div>

                <div className="col-6 terms-text">
                  <p>I accept to the Terms & Conditions regarding my personal data.</p>
                </div>

                <button className="col-4 contact-form-button" onClick={this.sendMessageToEmail}>SKICKA</button>
              </div>
	         </form>
        </div>
      )
  }
}

export default ContactForm;
