import React, { Component } from 'react';

class ContactForm extends Component {
  constructor(props){
    super(props);

    this.sendMessageToEmail = this.sendMessageToEmail.bind(this);
  }

  sendMessageToEmail() {

  let data = {
      name: this.state.name,
      email: this.state.email,
      phoneNumber: this.state.phoneNumber,
      startDate: date,
      time: this.state.time,
      guests: this.state.guests
  }

  return fetch('http://localhost:8888/phpfiles/create.php', {
    method: "POST",
    mode: "no-cors",
    body: JSON.stringify(data)
    })
    .then((response) => {
      this.confirmationPopUp();
    })
    .catch((error) => {
      console.error(error);
    })
}


  render() {
    return (

    	<div className="contact-form">
          <h2 className="second-section-title">Kontakta oss</h2>
	        <form>
              <div className="row">
                <div className="col-11 input-contact">
      	          <label>
                  <input type="text" name="name" placeholder="Namn" required />
                  </label>
                </div>
              </div>

              <div className="row"> 
              <div className="col-11 input-contact">
                <label>
                <input type="text" name="email" placeholder="Email" required />
                </label>
              </div>
              </div>

              <div className="row">
                <textarea className="col-11 input-contact" placeholder="Skriv ditt meddelande" required></textarea>
              </div>

              <div className="row">
                <div className="col-2 checkbox-gdpr">
                  <input className="checkbox" type="checkbox" required/>
                </div>

                <div className="col-6 terms-text">
                  <p>I accept to the Terms & Conditions regarding my personal data.</p>
                </div>

                <button className="col-4 contact-form-button">SKICKA</button>
              </div>
	         </form>
        </div>
      )
  }
}

export default ContactForm;
