import React, { Component } from 'react';
import FoodMenu from '../Assets/JSON/FoodMenu.json';

class CardMenu extends Component{
  constructor(props){
    super(props);
  }

  componentWillMount() {
    const { menuIndex } = this.props;

    this.setState({
      foodMenu: FoodMenu.FoodMenus[menuIndex],
      menuMainTitle: ["Milkshakes", "Våfflor", "Wraffles", "Dryck", "Bakverk"]
    })
  }

  render() {

    const { closeMenu } = this.props;

    return (

      <div className="slide-in-elliptic-top-fwd menu-card">
        {this.state.menuMainTitle.map((mainTitle, index) => {
           return (
          <h2 className="menu-main-title">{mainTitle}</h2>
          )
        })}

        {this.state.foodMenu.map((menu, index) => {
           return (
            <div className="menu-content" onClick = {closeMenu}>
              <h3 className="menu-title">{menu.title}</h3>
              <p className="menu-price">{menu.price}</p>
            </div>
            )
       })}
      </div>

      )
  }
} 

export default CardMenu;
